mapper.insert:
INSERT INTO account (
                      user_id
                    , user_pw
                    , user_name
)
VALUES (
           #{userId}
       , #{userPw}
       , #{userName}
       );

INSERT INTO article (
    article_title
    , article_contents
    , user_no
    , category_no
    , create_dt
    , edit_dt
    , view_cnt
    , thumbnail_url
) VALUES (
    #{articleTitle}
    , #{articleContents}
    , #{userNo}
    , #{categoryNo}
    , NOW()
    , NOW()
    , 0
    , null
)

mapper.update:
UPDATE
    article
SET
    article_title = #{articleTitle}
    , article_contents = #{articleContents}
    , category_no = #{categoryNo}
    , thumbnail_url = #{thumbnailUrl}
    , edit_dt = now()
WHERE
    article_no = #{articleNo}

mapper.select:
SELECT * FROM article
WHERE article_title like CONCAT('%', #{keyword}, '%')
ORDER BY create_dt DESC OFFSET #{page} LIMIT #{pageSize}
